package com.syntax.class01;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//data Tape name=value
		
		byte shelf1 = 127;
		short shelf2 = 32767;
		int shelf3 = -1000000;
		long shelf4 = 5555444433332222l;
		
		float shelf5 = 3.333f;
		double shelf6 = 88.88;
		
		char shelf7 = 'A';
	    char shelf8 = '%';
		
	    boolean Saturday = true;
	    boolean Monday = false;
	    
	    shelf1 = 100;
	    shelf2 = 30000;
	    shelf3 = 1000000;
	    Saturday = false;
	    
	    
	    
	    //; Printing data
	    
	    System.out.println(shelf1);
	    System.out.println(shelf2);
	    System.out.println(shelf3);
	    System.out.println(shelf4);
	    System.out.println(shelf5);
	    System.out.println(shelf6);
	    System.out.println(shelf7);
	    System.out.println(shelf8);
	    
	    System.out.println(Saturday);
	    System.out.println(Monday);
	    
	    
		
	}

}
